CREATE TABLE IF NOT EXISTS `{prefix}forum_category` (`id` int(11) NOT NULL AUTO_INCREMENT,`category_title` varchar(255) NOT NULL,`category_desc` varchar(255) NOT NULL,`category_place` int(11) NOT NULL DEFAULT '0',PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;
INSERT INTO `{prefix}forum_category` (`id`, `category_title`, `category_desc`, `category_place`) VALUES (1, 'First Category', 'First category created.', 0);
CREATE TABLE IF NOT EXISTS `{prefix}forum_node` (`id` int(11) NOT NULL AUTO_INCREMENT,`node_name` varchar(255) NOT NULL,`name_friendly` varchar(255) NOT NULL,`node_desc` varchar(255) NOT NULL,`in_category` int(11) NOT NULL,`node_locked` int(11) NOT NULL DEFAULT '0',`node_place` int(11) NOT NULL DEFAULT '0',PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;
INSERT INTO `{prefix}forum_node` (`id`, `node_name`, `name_friendly`, `node_desc`, `in_category`, `node_locked`, `node_place`) VALUES (1, 'First Node', 'first_node', 'First node created.', 1, 0, 0);
CREATE TABLE IF NOT EXISTS `{prefix}forum_posts` (`id` int(11) NOT NULL AUTO_INCREMENT,`post_title` varchar(255) NOT NULL DEFAULT '',`title_friendly` varchar(255) NOT NULL,`post_content` text NOT NULL,`post_tags` varchar(255) NOT NULL,`post_time` int(11) NOT NULL,`post_user` int(11) NOT NULL,`origin_thread` int(11) NOT NULL DEFAULT '0',`origin_node` int(11) NOT NULL DEFAULT '0',`post_type` int(11) NOT NULL,`post_sticky` int(11) NOT NULL DEFAULT '0',`post_locked` int(11) NOT NULL DEFAULT '0',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `{prefix}generic` (`id` int(11) NOT NULL AUTO_INCREMENT,`site_name` varchar(255) NOT NULL,`site_theme` varchar(255) NOT NULL,`site_language` varchar(255) NOT NULL,`site_email` varchar(255) NOT NULL,`register_email_activate` int(11) NOT NULL DEFAULT '0',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `{prefix}permissions` (`id` int(11) NOT NULL AUTO_INCREMENT,`permission_name` varchar(255) NOT NULL,PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=6 ;
INSERT INTO `{prefix}permissions` (`id`, `permission_name`) VALUES (1, 'view_forum'),(2, 'create_thread'),(3, 'reply_thread'),(4, 'access_moderation'),(5, 'access_administration');
CREATE TABLE IF NOT EXISTS `{prefix}reports` (`id` int(11) NOT NULL AUTO_INCREMENT,`report_reason` varchar(255) NOT NULL,`reported_by` int(11) NOT NULL,`reported_user` int(11) NOT NULL DEFAULT '0',`reported_post` int(11) NOT NULL DEFAULT '0',`reported_time` int(11) NOT NULL,`report_close` int(11) NOT NULL DEFAULT '0',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `{prefix}usergroups` (`id` int(11) NOT NULL AUTO_INCREMENT,`group_name` varchar(255) NOT NULL,`group_style` varchar(255) NOT NULL DEFAULT '%username%',`group_permissions` varchar(255) NOT NULL DEFAULT '0',PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=6 ;
INSERT INTO `{prefix}usergroups` (`id`, `group_name`, `group_style`, `group_permissions`) VALUES (1, 'User', '<span>%username%</span>', '1,2,3'),(2, 'Banned', '<span>%username% (Banned)</span>', '0'),(3, 'Moderator', '<span style="color:#3a5892;"><strong>%username%</strong></span>', '1,2,3,4'),(4, 'Administrator', '<span style="color:#762727;"><strong>%username%</strong></span>', '*');
CREATE TABLE IF NOT EXISTS `{prefix}users` (`id` int(11) NOT NULL AUTO_INCREMENT,`username` varchar(255) NOT NULL,`user_password` varchar(255) NOT NULL,`user_email` varchar(255) NOT NULL,`user_message` varchar(255) NOT NULL DEFAULT 'User',`user_avatar` varchar(255) NOT NULL DEFAULT 'default.png',`user_signature` varchar(255) NOT NULL,`date_joined` int(11) NOT NULL,`user_birthday` date NOT NULL,`user_group` int(11) NOT NULL DEFAULT '1',`user_disabled` int(11) NOT NULL DEFAULT '0',`is_banned` int(11) NOT NULL DEFAULT '0',`unban_time` int(11) NOT NULL,`ban_reason` varchar(255) NOT NULL DEFAULT 'None',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
